import 'dart:math';

/// 424. Longest Repeating Character Replacement
/// https://leetcode.com/problems/longest-repeating-character-replacement/
///
/// You are given a string s and an integer k. You can choose any character
/// of the string and change it to any other uppercase English character.
/// You can perform this operation at most k times.
///
/// Return the length of the longest substring containing the same letter
/// you can get after performing the above operations.

void main() {
  final s =
      "";
  // final s = 'A';
  print('solution: ${Solution().characterReplacement(s, 7520)}');
}

// todo Оптимизировать, не проходит по Time Limit Exceeded
// todo идея не использовать строки, а unitCode
class Solution {
  int characterReplacement(String s, int k) {
    final freq = <String, int>{};

    int maxWindow = 0;
    int currentWindow = 0;
    int start = 0;
    while (start <= s.length - currentWindow) {
      int maxFreq = 0;
      for (var i = start; i < start + currentWindow; i++) {
        maxFreq = max(maxFreq, freq[s[i]]!);
      }

      final placeholders = currentWindow - maxFreq;

      if (placeholders <= k) {
        maxWindow = max(maxWindow, currentWindow);
        if (start + currentWindow < s.length) {
          freq.update(s[start + currentWindow], (val) => val + 1,
              ifAbsent: () => 1);
        }
        currentWindow++;
      } else {
        freq.update(s[start], (val) => val - 1);
        if (start + currentWindow < s.length) {
          freq.update(s[start + currentWindow], (val) => val + 1,
              ifAbsent: () => 1);
        }
        start++;
      }
    }

    return maxWindow;
  }
}

